mapper.update:
update member set 
	
		m_pwd = #{m_pwd},
	
	m_name = #{m_name},
	m_tel = #{m_tel},
	m_email = #{m_email}
where m_no = #{m_no}


update member_addr set 
	m_addr = #{m_addr}
where m_no = #{m_no} and (select min(ma_no) as mno from member_addr group by(m_no) having m_no = #{m_no}) = ma_no
